    position: "absolute",
    top: "50%",
    left: "7.5%",
    color: "white",
    width: "50em",
    fontSize: "1rem",
  };
  return (
    <>
      <div style={contaner_style}>
        <img src={header} alt="" id="banner" style={bannerstyle} />
